mapper.select:
  		select count(*)
			,ifnull(sum(case when cancel != '' AND cancel <= ${oCsno} then 1 end),'0') as ccnt
		FROM gd_order_item WHERE ordno=${oCordno}

select ifnull(sum(remoney), '0') as remoney from gd_order_item a 
	left join gd_order_cancel b on a.cancel = b.sno 
	where a.ordno = ${oCordno}

mapper.update:
UPDATE gd_banner 
SET title=#{title}, img=#{img}, imgmobile=#{imgMobile}, linkaddr=#{linkaddr}, target=#{target}, sort=#{sort}, used=#{used}  
WHERE sno = #{sno}

UPDATE gd_coupon_applymember
   SET used=0 ,applyDt=null ,ordno=0
 WHERE applysno=#{applysno} AND ordno=#{ordno}

mapper.select:
SELECT SUM(a.deliveryPrice) delivery ,SUM(a.addDeliveryPrice) addDelivery FROM( SELECT DISTINCT T2.deliveryPrice, T2.addDeliveryPrice FROM gd_order_item T1 JOIN gd_log_cancel T2 ON T1.cancel=T2.cancel JOIN gd_goods T3 ON T1.goodsno=T3.goodsno WHERE T1.ordno=#{ordno} AND T3.sellerCd IN
#{sellerCd}
) a

SELECT DISTINCT IFNULL(T2.sellerCd,'admin')
  FROM gd_order_item T1 JOIN gd_goods T2 ON T1.goodsno=T2.goodsno
 WHERE T1.ordno=#{ordno}

mapper.update:
UPDATE gd_order
   SET emoney=#{emoney}
 WHERE ordno=#{ordno}

UPDATE gd_order
   SET reserve=#{reserve}
 WHERE ordno=#{ordno}
