mapper.select:
SELECT * FROM gd_conf_set WHERE 1=1 

	AND conf_cd = #{conf_cd}


	AND dim_set = #{dim_set}


	AND dim_set = #{dim1_var}


	AND dim1_var = #{dim1_var}


	AND dim1_var = #{dim2_var}

mapper.insert:
		INSERT INTO 
			gd_conf_set (conf_cd, dim_set, dim1_var, dim2_var, dim_val, regdt, uptdt) 
		VALUES (


                    "config"


                  	#{conf_cd}


			, 


                    "cfg"


                  	#{dim_set}


			, #{dim1_var}
			, 


                    "N/A"


                  	#{dim2_var}


            , #{dim_val}
            , NOW()
            , NOW()
		) ON DUPLICATE KEY UPDATE dim_val = #{dim_val}

mapper.update:
UPDATE gd_conf_set
	SET
dim_val = #{dimVal}
WHERE 
	conf_cd = #{confCd}
	AND dim_set = #{dimSet}
	AND dim1_var = #{dim1Var}

mapper.select:
SELECT 
	dim_val 
	FROM gd_conf_set
	WHERE 1 = 1
	AND conf_cd = #{confCd}
	AND dim_set = #{dimSet}
	AND dim1_var = #{dim1Var}
